class Minesweeper{constructor(target,width,height,bombsPercentage){this.target=target,this.DOM=null,this.width=width,this.height=height,this.bombsPercentage=bombsPercentage,this.bombsCount=1,this.init()}init(){this.validate(),this.render()}validate(){const DOM=document.querySelector(this.target);if(!DOM)throw"Kritine klaida, reikia nurodyti tinkama selectoriu kur generuoti zaidima!";if(this.DOM=DOM,"number"!=typeof this.width||this.width<1||this.width%1>0)throw"Netinkamas plotis!";if("number"!=typeof this.height||this.height<1||this.height%1>0)throw"Netinkamas aukstis!";if(this.width*this.height<2)throw"Bendras lentos plotas yra per mazas.";if("number"!=typeof this.bombsPercentage||this.bombsPercentage<=0||this.bombsPercentage>=100)throw"Netinkamas bombu kiekis!";const bombsCount=Math.floor(this.width*this.height*this.bombsPercentage/100);bombsCount>0&&(this.bombsCount=bombsCount)}render(){let HTML='<div class="header">\n                        <div class="bombs">099</div>\n                        <div class="smile">:)</div>\n                        <div class="timer">000</div>\n                    </div>\n                    <div class="field">\n                        <div class="cell">C</div>\n                        <div class="cell">C</div>\n                        <div class="cell">C</div>\n                        <div class="cell">C</div>\n                        <div class="cell">C</div>\n                        <div class="cell">C</div>\n                        <div class="cell">C</div>\n                    </div>';this.DOM.classList.add("minesweeper"),this.DOM.innerHTML=HTML}}const game=new Minesweeper("#game",10,10,15);console.log(game);